from tkinter import *from tkinter import filedialogfrom PIL import ImageTk, Imageimport osroot = Tk()root.geometry("600x400")open_img = ImageTk.PhotoImage(Image.open ("open.png"))save_img = ImageTk.PhotoImage(Image.open ("save.png"))exit_img = ImageTk.PhotoImage(Image.open ("exit.jpg"))label = Label(root, text="File name")label.place(relx=0.28,rely=0.03,anchor= CENTER)text= Text(root,height=35,width=80)text.place(relx=0.5,rely=0.55,anchor= CENTER)input_file_name = Entry(root)input_file_name.place(relx=0.50,rely=0.03, anchor= CENTER)root.mainloop()